function getDeleteSpan(id) {
    return document.querySelector("#deleteSpan_" + id);
}

function getConfirmDeleteSpan(id) {
    return document.querySelector("#confirmDeleteSpan_" + id);
}

function deleteButtonClick(id) {
    getDeleteSpan(id).classList.add("d-none");
    getConfirmDeleteSpan(id).classList.remove("d-none");
}

function noButtonClick(id) {
    getDeleteSpan(id).classList.remove("d-none");
    getConfirmDeleteSpan(id).classList.add("d-none");
}